require(__dirname).test(
  { xml : "<root xml:lang='en'/>"
  , expect :
    [ [ "attribute"
      , { name: "xml:lang"
        , local: "lang"
        , prefix: "xml"
        , uri: "http://www.w3.org/XML/1998/namespace"
        , value: "en"
        }
      ]
    , [ "opentag"
      , { name: "root"
        , uri: ""
        , prefix: ""
        , local: "root"
        , attributes:
          { "xml:lang":
            { name: "xml:lang"
            , local: "lang"
            , prefix: "xml"
            , uri: "http://www.w3.org/XML/1998/namespace"
            , value: "en"
            }
          }
        , ns: {}
        }
      ]
    , ["closetag", "root"]
    ]
  , strict : true
  , opt : { xmlns: true }
  }
)

